package forloopStar;

public class ForloopStar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 // a java program to display a triangle with stars
     for(int c=0;c<5;c++)
     {
    	 for(int v=0;v<c;v++)
    	 {
    		 System.out.print("*");
    	 }
    	 System.out.println();
     }
	}

}
